import Phaser from "phaser";
export default class MathFighter extends Phaser.Scene{

    constructor(){
        super('math-fighter-scene')
    }

    init(){
        this.gameHalfWidth = this.scale.width * 0.5
        this.gameHalfHeight = this.scale.height * 0.5
        this.player = undefined
        this.enemy = undefined
        this.slash = undefined
        this.startGame = false
        this.questionText = undefined
        this.resultText = undefined
        this.button1 = undefined
        this.button2 = undefined
        this.button3 = undefined
        this.button4 = undefined
        this.button5 = undefined
        this.button6 = undefined
        this.button7 = undefined
        this.button8 = undefined
        this.button9 = undefined
        this.button0 = undefined
        this.buttonDel1 = undefined
        this.buttonOk = undefined
        this.numberArray = []
        this.number = 0
        this.question = []
        this.corretAnswer = undefined
        this.playerAttack = false
        this.enemyAttack = false


    }

    preload(){
        this.load.image('bg', 'images/bg_layer1.png')
        this.load.image('tile', 'images/tile.png')
        this.load.image('start-btn', 'images/start.png')
        this.load.image('fight-bg', 'images/fight-bg.png')

        this.load.spritesheet('player', 'images/warrior1.png', {frameHeight:80,frameWidth:80})
        this.load.spritesheet('enemy', 'images/warrior2.png', {frameHeight:80, frameWidth:80})
        this.load.spritesheet('numbers', 'images/numbers.png', {frameHeight:71.25, frameWidth:131})
        this.load.spritesheet('slash', 'images/slash.png', {frameHeight:88,frameWidth:42})
    }

    create(){
        this.add.image(240,320, 'bg')
        // @ts-ignore
        this.questionText = this.add.text(this.gameHalfWidth, 100, '0', {fontSize:'42px', fill:'#722F37', fontFamily:'Arial, sans-serif'})
        // @ts-ignore
        this.resultText = this.add.text(this.gameHalfWidth, 90, '0', {fontSize:'32px', fill:'#FF0000', fontFamily:'Arial, sans-serif'})
        this.generateQuestion()
        this.createButtons()
        this.addNumber()

    }

    update(time){

    }


    createButtons(){
        const startPositionY = this.scale.height - 246
        const widthDiffrence = 136
        const hightDiffrence = 75.25

        // tengah 
        this.button2 = this.add.image(this.gameHalfWidth, startPositionY, 'numbers', 1).setInteractive().setData('value', 2)
        this.button5 = this.add.image(this.gameHalfWidth, this.button2.y + hightDiffrence, 'numbers', 4).setInteractive().setData('value', 5)
        this.button8 = this.add.image(this.gameHalfWidth, this.button5.y + hightDiffrence, 'numbers', 7).setInteractive().setData('value', 8)
        this.button0 = this.add.image(this.gameHalfWidth, this.button8.y + hightDiffrence, 'numbers', 10).setInteractive().setData('value', 0)

        // samping kiri 
        this.button1 = this.add.image(this.button2.x - widthDiffrence, startPositionY, 'numbers', 0).setInteractive().setData('value', 1)
        this.button4 = this.add.image(this.button5.x - widthDiffrence, this.button1.y + hightDiffrence, 'numbers', 3).setInteractive().setData('value', 4)
        this.button7 = this.add.image(this.button8.x - widthDiffrence, this.button4.y + hightDiffrence, 'numbers', 6).setInteractive().setData('value', 7)
        this.buttonDel1 = this.add.image(this.button0.x - widthDiffrence, this.button7.y + hightDiffrence, 'numbers', 9).setInteractive().setData('value', 'del')

        // samping kanan 
        this.button3 = this.add.image(this.button2.x + widthDiffrence, startPositionY, 'numbers', 2).setInteractive().setData('value', 3)
        this.button6 = this.add.image(this.button5.x  + widthDiffrence, this.button3.y + hightDiffrence, 'numbers', 5).setInteractive().setData('value', 6)
        this.button9 = this.add.image(this.button8.x + widthDiffrence, this.button6.y + hightDiffrence, 'numbers', 8).setInteractive().setData('value', 9)
        this.buttonOk = this.add.image(this.button0.x + widthDiffrence, this.button9.y + hightDiffrence, 'numbers', 11).setInteractive().setData('value', 'ok')

    }

    addNumber(pointer, object, event){
        let value = object.getData('value')

        if (isNaN (value) ){

            if ( value == 'del' ){
                this.numberArray.pop()

                if (this.numberArray.length < 1){
                    this.numberArray[0] = 0
                }
            }

            if ( value == 'ok'){
                this.checkAnswer()
                this.numberArray = []
                this.numberArray[0] = 0 
            }
        }else{
            if ( this.numberArray.length == 1 && this.numberArray[0] == 0){
                this.numberArray[0] = value
            }else{
                if (this.numberArray.length < 10){
                    this.numberArray.push(value)
                }
            }
        }
        this.number = String(this.numberArray.join(''))

        this.resultText.setText(this.number)
        const textHalfWidth = this.resultText.width * 0.5
        this.resultText.setX(this.gameHalfWidth - textHalfWidth)
        event.stopPropagation()
    }

    getOperator(){
        const operator = ['+', '-', 'x', ':']
        return operator [Phaser.Math.Between(0, operator.length - 1)]
    }

    generateQuestion(){
        let numberA = 'apa'
        let numberB = Phaser.Math.Between(0, 50)
        let operator = this.getOperator()

        if ( operator === '+' ){
            this.question[0] = `${numberA} + ${numberB} `
            this.question[1] = numberA + numberB
        }

        if ( operator === '-'){
            if (numberB > numberA){
                this.question[0] = `${numberB} - ${numberA}`
                this.question[1] = numberB - numberA
            } else {
                this.question[0] = `${numberA} - ${numberB}`
                this.question[1] = numberA - numberB
            }
        }

        if ( operator === 'x'){
            this.question[0] = `${numberA} x ${numberB}`
            this.question[1] = numberA * numberB
        }

        if ( operator === ':'){
            do {
                numberA = Phaser.Math.Between(0, 50)
                numberB = Phaser.Math.Between(0, 50)
            } 

            while (!Number.isInteger(numberA/numberB))
            this.question[0] = `${numberA} : ${numberB}`
            this.question[1] = numberA / numberB
        }
        this.questionText.setText(this.question[0])
        const textHalfWidth = this.questionText.width * 0.5
        this.questionText.setX(this.gameHalfWidth - textHalfWidth)

    }

    checkAnswer(){
        if ( this.number == this.question[1]){
            this.corretAnswer = true
        } 
        else {
            this.corretAnswer = false
        }
    }
}
